import{_ as t,W as e,X as l,Y as r}from"./framework-6ce913bb.js";const n={},d=r('<h2 id="linux-系统初始化进程" tabindex="-1"><a class="header-anchor" href="#linux-系统初始化进程" aria-hidden="true">#</a> Linux 系统初始化进程</h2><table><thead><tr><th style="text-align:center;">System V init 运行级别</th><th style="text-align:center;">Systemd 目标名称</th><th style="text-align:center;">Systemd 目标作用</th></tr></thead><tbody><tr><td style="text-align:center;">0</td><td style="text-align:center;">poweroff.target</td><td style="text-align:center;">关机</td></tr><tr><td style="text-align:center;">1</td><td style="text-align:center;">rescue.target</td><td style="text-align:center;">单用户模式</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">multi-user.target</td><td style="text-align:center;">多用户的文本界面</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">multi-user.target</td><td style="text-align:center;">多用户的文本界面</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">multi-user.target</td><td style="text-align:center;">多用户的文本界面</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">graphical.target</td><td style="text-align:center;">多用户的图形界面</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:center;">reboot.target</td><td style="text-align:center;">重启</td></tr><tr><td style="text-align:center;">7</td><td style="text-align:center;">emergency.target</td><td style="text-align:center;">救援模式</td></tr></tbody></table><blockquote><p>RHEL 5/6 系统使用 service、chkconfig 等命令来管理系统服务，RHEL 7/8 系统使用 systemctl 命令来管理系统服务</p></blockquote><h2 id="服务启动、重启、停止、重载、查看状态等常用命令" tabindex="-1"><a class="header-anchor" href="#服务启动、重启、停止、重载、查看状态等常用命令" aria-hidden="true">#</a> 服务启动、重启、停止、重载、查看状态等常用命令：</h2><table><thead><tr><th style="text-align:center;">RHEL 5/6 系统</th><th style="text-align:center;">RHEL 7/8 系统</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">service foo start</td><td style="text-align:center;">systemctl start httpd</td><td style="text-align:center;">启动服务</td></tr><tr><td style="text-align:center;">service foo restart</td><td style="text-align:center;">systemctl restart httpd</td><td style="text-align:center;">重启服务</td></tr><tr><td style="text-align:center;">service foo stop</td><td style="text-align:center;">systemctl stop httpd</td><td style="text-align:center;">停止服务</td></tr><tr><td style="text-align:center;">service foo reload</td><td style="text-align:center;">systemctl reload httpd</td><td style="text-align:center;">重新加载配置文件（不终止服务）</td></tr><tr><td style="text-align:center;">service foo status</td><td style="text-align:center;">systemctl status httpd</td><td style="text-align:center;">查看服务状态</td></tr></tbody></table><h2 id="服务开机启动、开机不启动、查看各级别下服务启动状态等常用命令" tabindex="-1"><a class="header-anchor" href="#服务开机启动、开机不启动、查看各级别下服务启动状态等常用命令" aria-hidden="true">#</a> 服务开机启动、开机不启动、查看各级别下服务启动状态等常用命令：</h2><table><thead><tr><th style="text-align:center;">RHEL 5/6 系统</th><th style="text-align:center;">RHEL 7/8 系统</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">chkconfig foo on</td><td style="text-align:center;">systemctl enable httpd</td><td style="text-align:center;">开机自动启动</td></tr><tr><td style="text-align:center;">chkconfig foo off</td><td style="text-align:center;">systemctl disable httpd</td><td style="text-align:center;">开机不自动启动</td></tr><tr><td style="text-align:center;">chkconfig foo</td><td style="text-align:center;">systemctl is-enabled httpd</td><td style="text-align:center;">查看特定服务是否为开机自启动</td></tr><tr><td style="text-align:center;">chkconfig --list</td><td style="text-align:center;">systemctl list-unit-files-type=httpd</td><td style="text-align:center;">查看各个级别下服务的启动与禁用情况</td></tr></tbody></table>',7),s=[d];function a(c,i){return e(),l("div",null,s)}const g=t(n,[["render",a],["__file","system_service.html.vue"]]);export{g as default};
